contracts/secretShop/ERC721Delegate.sol.content:
// SPDX-License-Identifier: GPL-3.0-only

pragma solidity 0.8.15;

import '@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol';
import '@openzeppelin/contracts/token/ERC721/IERC721.sol';
import '@openzeppelin/contracts/access/AccessControl.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import '@openzeppelin/contracts/security/Pausable.sol';
import './MarketConsts.sol';
import './interfaces/IDelegate.sol';

contract ERC721Delegate is IDelegate, AccessControl, IERC721Receiver, ReentrancyGuard, Pausable {
  bytes32 public constant DELEGATION_CALLER = keccak256('DELEGATION_CALLER');
  bytes32 public constant PAUSABLE_CALLER = keccak256('PAUSABLE_CALLER');

  /**
   * @dev single item data
   */
  struct Pair {
    uint256 salt;
    IERC721 token;
    uint256 tokenId;
  }

  constructor() {
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
  }

  /**
   * @return delegateType the delegate's type
   */
  function delegateType() external pure override returns (uint256) {
    return uint256(Market.DelegationType.ERC721);
  }

  /**
   * @dev Received function
   */
  function onERC721Received(
    address,
    address,
    uint256,
    bytes calldata
  ) external pure override returns (bytes4) {
    return this.onERC721Received.selector;
  }

  function pause() public onlyRole(PAUSABLE_CALLER) {
    _pause();
  }

  function unpause() public onlyRole(PAUSABLE_CALLER) {
    _unpause();
  }

  /**
   * @dev decode data to the array of Pair
   */
  function decode(bytes calldata data) public pure returns (Pair[] memory) {
    return abi.decode(data, (Pair[]));
  }

  /**
   * @dev run the sell to transfer item
   * @param seller address which sell the item
   * @param buyer address which buy the item
   * @param data the item's data, which will be decode as a array of Pair
   */
  function executeSell(
    address seller,
    address buyer,
    bytes calldata data
  ) public override nonReentrant onlyRole(DELEGATION_CALLER) whenNotPaused returns (bool) {
    Pair[] memory pairs = decode(data);
    for (uint256 i = 0; i < pairs.length; i++) {
      Pair memory p = pairs[i];
      p.token.safeTransferFrom(seller, buyer, p.tokenId);
    }
    return true;
  }
}

contracts/secretShop/MarketConsts.sol.content:
// SPDX-License-Identifier: GPL-3.0-only

pragma solidity 0.8.15;

import './interfaces/IDelegate.sol';
import './interfaces/IWETHUpgradable.sol';
import '@openzeppelin/contracts/token/ERC1155/IERC1155.sol';

library Market {
  uint256 public constant INTENT_SELL = 1;
  uint256 public constant INTENT_BUY = 2;

  uint8 public constant SIGN_V1 = 1;

  struct OrderItem {
    uint256 price;
    /** why bytes: because struct too complex will be omitted */
    bytes data;
  }

  // An Order
  struct Order {
    /* salt, a random number */
    uint256 salt;
    /* address which create order */
    address user;
    /** ChainId explain which network */
    uint256 network;
    /** which intent, 1 for sell */
    uint256 intent;
    /** just 1 at v1 */
    uint256 delegateType;
    /** order end ddl */
    uint256 deadline;
    /**
      address of the ERC20 coin to trade
    */
    IERC20Upgradeable currency;
    /**
      items in an Order
    */
    OrderItem[] items;
    /**
      signature for Order, eip 2098 would be better
    */
    bytes32 r;
    bytes32 s;
    uint8 v;
    /**
      could be 1 now
    */
    uint8 signVersion;
  }

  struct Fee {
    uint256 percentage;
    address to;
  }

  struct SettleDetail {
    // order operation type
    Market.Op op;
    //
    uint256 orderIdx;
    //
    uint256 itemIdx;
    //
    uint256 price;
    //
    bytes32 itemHash;
    // delegate which address to transfer token
    IDelegate executionDelegate;
    Fee[] fees;
  }

  /**
   * @dev information from who send this tx
   */
  struct SettleShared {
    uint256 salt;
    uint256 deadline;
    address user;
    /**
     * can one order fail
     * if true, tx will revert if one order fail
     * else, tx didn't fail if one
     */
    bool canFail;
  }

  struct RunInput {
    // one Order match one SettleDetail
    Order[] orders;
    SettleDetail[] details;
    SettleShared shared;
  }

  enum InvStatus {
    NEW,
    COMPLETE,
    CANCELLED
  }

  /**
    Operation
  */
  enum Op {
    INVALID,
    COMPLETE_SELL_OFFER,
    COMPLETE_BUY_OFFER,
    CANCEL_OFFER
  }

  enum DelegationType {
    INVALID,
    ERC1155,
    ERC721
  }
}

