contracts/assetFactory/P12AssetFactoryUp.content:
// SPDX-License-Identifier: GPL-3.0-only
pragma solidity 0.8.15;

import '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol';
import '@openzeppelin/contracts/utils/cryptography/ECDSA.sol';
import './P12Asset.sol';
import '../factory/P12CoinFactoryUpgradeable.sol';
import './interfaces/IP12AssetFactoryUpgradable.sol';
import './P12AssetFactoryStorage.sol';
import '../access/SafeOwnableUpgradeable.sol';

contract P12AssetFactoryUpgradable is
  P12AssetFactoryStorage,
  IP12AssetFactoryUpgradable,
  ReentrancyGuardUpgradeable,
  SafeOwnableUpgradeable,
  PausableUpgradeable,
  UUPSUpgradeable
{
  // ============ External ============

  /**
  @notice set new p12CoinFactory
  @param newP12CoinFactory address of p12CoinFactory
   */
  function setP12CoinFactory(address newP12CoinFactory) external virtual override onlyOwner {
    address oldP12Factory = p12CoinFactory;
    require(newP12CoinFactory != address(0), 'P12AssetF: p12CoinFactory cannot be 0');
    p12CoinFactory = newP12CoinFactory;
    emit SetP12Factory(oldP12Factory, newP12CoinFactory);
  }

  function pause() public onlyOwner {
    _pause();
  }

  function unpause() public onlyOwner {
    _unpause();
  }

  function initialize(address p12CoinFactory_) public initializer {
    require(p12CoinFactory_ != address(0), 'P12AssetF: p12CoinFactory cannot be 0');
    p12CoinFactory = p12CoinFactory_;

    __ReentrancyGuard_init_unchained();
    __Pausable_init_unchained();
    __Ownable_init_unchained();
  }

  /**
   * @dev create Collection
   * @param gameId a off-chain game id
   * @param contractURI contract-level metadata uri
   */
  function createCollection(string calldata gameId, string calldata contractURI)
    public
    override
    onlyDeveloper(gameId)
    whenNotPaused
  {
    P12Asset collection = new P12Asset(contractURI);
    // record creator
    registry[address(collection)] = gameId;

    emit CollectionCreated(address(collection), msg.sender);
  }

  /**
   * @dev create asset and mint to msg.sender address
   * @param collection which collection want to create
   * @param amount amount of asset
   * @param uri new asset's metadata uri
   */
  function createAssetAndMint(
    address collection,
    uint256 amount,
    string calldata uri
  ) public override onlyCollectionDeveloper(collection) whenNotPaused nonReentrant {
    // create
    uint256 tokenId = P12Asset(collection).create(amount, uri);
    // mint to developer address
    P12Asset(collection).mint(msg.sender, tokenId, amount, new bytes(0));

    emit SftCreated(address(collection), tokenId, amount);
  }

  /**
   * @dev update Collection Uri
   * @param collection collection address
   * @param newUri new Contract-level metadata uri
   */
  function updateCollectionUri(address collection, string calldata newUri)
    public
    override
    onlyCollectionDeveloper(collection)
    whenNotPaused
  {
    P12Asset(collection).setContractURI(newUri);
  }

  /**
   * @dev update Sft Uri
   * @param collection collection address
   * @param tokenId token id
   * @param newUri new metadata uri
   */
  function updateSftUri(
    address collection,
    uint256 tokenId,
    string calldata newUri
  ) public override onlyCollectionDeveloper(collection) whenNotPaused {
    P12Asset(collection).setUri(tokenId, newUri);
  }

  /** upgrade function */
  function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

  modifier onlyDeveloper(string memory gameId) {
    require(P12CoinFactoryUpgradeable(p12CoinFactory).allGames(gameId) == msg.sender, 'P12AssetF: not game developer');
    _;
  }

  modifier onlyCollectionDeveloper(address collection) {
    require(
      P12CoinFactoryUpgradeable(p12CoinFactory).allGames(registry[collection]) == msg.sender,
      'P12AssetF: not game developer'
    );
    _;
  }
}

contracts/assetFactory/P12Asset.sol.content:
// SPDX-License-Identifier: GPL-3.0-only

pragma solidity 0.8.15;

import './interfaces/IP12Asset.sol';
import '@openzeppelin/contracts/token/ERC1155/ERC1155.sol';
import '../access/SafeOwnable.sol';

contract P12Asset is IP12Asset, ERC1155(''), SafeOwnable {
  /**
   * @dev contract-level metadata uri, refer to https://docs.opensea.io/docs/contract-level-metadata
   */
  string public contractURI;

  /**
   * @dev current supply, how many a id are minted not.
   */
  mapping(uint256 => uint256) public supply;

  /**
   * @dev max supply, a token id has a max supply cap
   */
  mapping(uint256 => uint256) public maxSupply;
  /**
   * @dev token id index, which will increase one by one
   */
  uint256 private idx = 0;

  // Mapping from token ID to account balances
  mapping(uint256 => mapping(address => uint256)) private _balances;

  // metadata uri
  mapping(uint256 => string) private _uri;

  constructor(string memory contractURI_) {
    require(bytes(contractURI_).length != 0, 'P12Asset: empty contractURI');
    contractURI = contractURI_;
  }

  /**
   * @dev developer create an new asset
   * @param amount the new asset's totalSupply
   * @param newUri metadata uri of the asset
   * @return uint256 new asset's tokenId
   */

  function create(uint256 amount, string calldata newUri) public override onlyOwner returns (uint256) {
    // set tokenId totalSupply
    maxSupply[idx] = amount;
    // set metadata Uri
    _setUri(idx, newUri);
    // idx increment
    idx += 1;
    return idx - 1;
  }

  /**
   * @dev update token's metadata uri
   * @param id tokenId
   * @param newUri new uri
   */
  function setUri(uint256 id, string calldata newUri) public override onlyOwner {
    _setUri(id, newUri);
  }

  /**
   * See {_mint}.
   */
  function mint(
    address to,
    uint256 id,
    uint256 amount,
    bytes memory data
  ) public override onlyOwner {
    require(id < idx, 'P12Asset: id is not valid');
    require(amount + supply[id] <= maxSupply[id], 'P12Asset: exceed max supply');
    _mint(to, id, amount, data);
    supply[id] += amount;
  }

  /**
   * @dev return token metadata uri
   * @param id token's id
   * @return uri metadata uri
   */
  function uri(uint256 id) public view virtual override returns (string memory) {
    require(id < idx, 'P12Asset: id not exist');
    return _uri[id];
  }

  /**
   * @dev set contract-level MetaData
   * @param newContractURI new Contract-level metadata uri
   */
  function setContractURI(string calldata newContractURI) public override onlyOwner {
    require(bytes(newContractURI).length != 0, 'P12Asset: empty contractURI');
    string memory oldContractURI = contractURI;
    contractURI = newContractURI;
    emit SetContractURI(oldContractURI, contractURI);
  }

  /**
   * @dev set token metadata uri
   * @param id  token id
   * @param newUri metadata uri
   */
  function _setUri(uint256 id, string calldata newUri) private {
    require(bytes(newUri).length != 0, 'P12Asset: empty uri');
    require(id <= idx, 'P12Asset: id not exist');
    _uri[id] = newUri;
    emit SetUri(id, newUri);
  }
}

